		} else if (p.NOT.OR) {
			result = LogicComparison.filterNotOr(p.NOT,query2,data,count + 1);
		} else if (p.NOT.IS) {
			result = SComparison.filterNotIS(p.NOT, query2, data, count + 1);
		} else if (p.NOT.GT) {
