		} else if (p.NOT.EQ) {
			result = MComparison.filterNotEQ(p.NOT, query2, data, count + 1);
		} else if (p.NOT.LT) {
			result = MComparison.filterNotLT(p.NOT, query2, data, count + 1);
		}

		if (count === 0 && result.length > 5000) {
			throw new ResultTooLargeError("> 5000");
