			if (all_matches(j[0],l,query2)) {
				j.push(l);
				pushed = true;
				break;
			}
		}
		if (!pushed) {
			let x: InsightResult[] = [];
			x.push(l);
			result2.push(x);
		}
	}

	return result2;
}

